mapper.insert:
INSERT INTO store_hashtag (
    id
    , hashtag_id
    , store_id
    , count
)
VALUES (
    store_hashtag_id_seq.nextval
    , #{hashtagId}
    , #{storeId}
    , 1
)

mapper.update:
UPDATE store_hashtag
SET count = count + 1
WHERE hashtag_id = #{hashtagId}
AND store_id = #{storeId}
