mapper.insert:
INSERT INTO review (
    id
    , member_id
    , store_id
    , score
    , content
)
VALUES (
    review_id_seq.nextval
    , #{review.memberId}
    , #{review.storeId}
    , #{review.score}
    , #{review.content}
)

mapper.update:
UPDATE store
SET avg_score = #{avgScore}
WHERE id = #{storeId}

UPDATE store
SET review_count = review_count + 1
WHERE id = #{storeId}
